    assign w_chan_spill_ready = mst_res_i.w_ready & (&w_sel_q);
    assign w_sel_load = mst_req_o.w_valid & mst_res_i.w_ready;
    assign w_sel_d    = sel_t'(w_sel_q + 1'b1);
